function i(){const e=document.getElementById("rightSidebarToggle"),d=document.getElementById("rightSidebar"),t=document.querySelector(".article-and-sidebar");if(!e||!d||!t)return;localStorage.getItem("rightSidebarHidden")==="true"&&(d.classList.add("hidden"),e.classList.add("toggled"),t.classList.add("sidebar-hidden")),e.addEventListener("click",()=>{d.classList.contains("hidden")?(d.classList.remove("hidden"),e.classList.remove("toggled"),t.classList.remove("sidebar-hidden"),localStorage.setItem("rightSidebarHidden","false")):(d.classList.add("hidden"),e.classList.add("toggled"),t.classList.add("sidebar-hidden"),localStorage.setItem("rightSidebarHidden","true"))})}document.readyState==="loading"?document.addEventListener("DOMContentLoaded",i):i();
